// n8n Code node — High-score filtering + formatted JSON output
// Place right after your HTTP Request (Apollo mixed_people/search).

/** ===================== TUNABLES ===================== **/
const ICP = {
  targetCountries: ['Egypt','Saudi Arabia','United Arab Emirates','Kuwait','Qatar','Oman','Bahrain'],
  targetIndustries: ['Retail','Ecommerce','Apparel','Fashion','Consumer Goods'],
  targetDepartments: ['Marketing','Growth','Ecommerce','Operations','IT','Engineering','Sales','Product','Digital'],
  titleKeywords: [
    'ecommerce','shopify','growth','digital','marketing','demand','performance',
    'revenue','retention','crm','automation','data','analytics','operations',
    'cto','cmo','coo','ceo','founder','owner','vp','director','head','lead','manager','product'
  ],
  idealCompanySize: [5, 1000], // inclusive
};

// Weights (sum ~= 100)
const W = {
  contactability: 30,   // email(10) + verified(12/4) + phone(4) + LinkedIn(4)
  seniority:     20,    // CxO 20 … IC 5
  titleKeywords: 12,    // up to 12 via keyword matches
  department:     8,    // dept in target list
  companySize:   10,    // inside ideal range = 10
  industry:       8,    // industry in target list
  geo:            6,    // country in target list
  webReach:       4,    // Alexa rank mapping
  recency:        2,    // updated within 12 months
};

// Emit controls: choose either/both
const FILTER = {
  topN: 20,       // emit top N leads after sorting DESC
  minScore: 70,   // and only those with score >= minScore
  // Return one item per lead with a compact, structured JSON
};
/** ===================================================== **/

// -------- helpers ----------
const norm = (s)=> (s ?? '').toString().trim().toLowerCase();
const hasAny = (text, keywords)=> {
  const t = norm(text);
  return keywords.filter(k => t.includes(norm(k)));
};
const withinRange = (n, [min,max])=> typeof n==='number' && n>=min && n<=max;
const yearDiff = (d)=>{
  const dt = d ? new Date(d) : null;
  if (!dt || isNaN(dt)) return Infinity;
  const ms = Date.now() - dt.getTime();
  return ms / (1000*60*60*24*365);
};
const alexaScore = (rank)=>{
  if (!rank || typeof rank !== 'number' || rank <= 0) return 0;
  if (rank <= 200_000) return 4;
  if (rank <= 500_000) return 3;
  if (rank <= 1_000_000) return 2;
  return 0;
};
const mapSeniority = (title, seniority)=>{
  const t = norm(title);
  const s = norm(seniority);
  const is = (...k)=> k.some(x => t.includes(x) || s.includes(x));
  if (is('ceo','chief executive','cxo','founder','co-founder','owner','partner','president')) return 20;
  if (is('cmo','cto','cio','coo','cfo','chief ')) return 20;
  if (is('vp','vice president')) return 18;
  if (is('director')) return 15;
  if (is('head','lead')) return 12;
  if (is('manager')) return 10;
  if (is('senior')) return 8;
  return 5; // IC/Unknown
};
const pickEmail = (person)=>{
  if (person.email) return { value: person.email, status: person.email_status || person.emailStatus, confidence: person.email_confidence };
  if (Array.isArray(person.emails) && person.emails.length){
    const sorted = [...person.emails].sort((a,b)=>{
      const aS = norm(a.status||a.state||'');
      const bS = norm(b.status||b.state||'');
      const order = (s)=> ['verified','valid','deliverable','guessed','unverified','unknown'].indexOf(s);
      return order(aS) - order(bS);
    });
    const top = sorted[0];
    return { value: top.value || top.email, status: top.status||top.state, confidence: top.confidence||top.confidence_level };
  }
  return {};
};
const phones = (p)=>{
  if (Array.isArray(p.phone_numbers) && p.phone_numbers.length) return p.phone_numbers;
  if (p.phone) return [p.phone];
  if (Array.isArray(p.phones) && p.phones.length) return p.phones;
  return [];
};
const orgOf = (p)=> p.organization || p.org || p.company || {};
const companySizeOf = (o)=> o.estimated_num_employees ?? o.employee_count ?? o.employees;

// Extract people from upstream items
function extractPeople(items){
  const out = [];
  for (const it of items){
    const j = it.json ?? {};
    if (Array.isArray(j.people)) out.push(...j.people);
    else if (Array.isArray(j.results)) out.push(...j.results);
    else if (j.person) out.push(j.person);
    else if (j.first_name || j.title || j.name) out.push(j); // already a person
  }
  return out;
}

// -------- scoring ----------
function scorePerson(p){
  const o = orgOf(p);

  // base fields
  const name = [p.first_name, p.last_name].filter(Boolean).join(' ') || p.name || '';
  const title = p.title || '';
  const seniority = p.seniority || '';
  const department = p.department || p.departments?.[0] || '';
  const country = p.country || p.location_country || p.country_name || '';
  const city = p.city || p.location_city || '';
  const linkedin = p.linkedin_url || p.linkedin || '';
  const orgName = o.name || o.organization_name || '';
  const website = o.website_url || o.primary_domain || o.domain || '';
  const industry = o.industry || '';
  const alexa = typeof (o.alexa_ranking ?? o.alexaRank ?? o.rank) === 'string'
    ? Number(o.alexa_ranking ?? o.alexaRank ?? o.rank)
    : (o.alexa_ranking ?? o.alexaRank ?? o.rank);
  const size = companySizeOf(o);
  const updYrs = yearDiff(p.updated_at || p.last_updated || p.last_activity_at || p.last_contacted_at);
  const tMatches = hasAny(title, ICP.titleKeywords);
  const emailObj = pickEmail(p);
  const phoneArr = phones(p);

  // Contactability (0–30)
  let cScore = 0; const cNotes = [];
  if (emailObj.value){ cScore += 10; cNotes.push(`Has email (${emailObj.value}) +10`); }
  const st = norm(emailObj.status||'');
  if (st.includes('verified') || st.includes('valid') || st.includes('deliverable')) { cScore += 12; cNotes.push(`Email status ${emailObj.status} +12`); }
  else if (st.includes('guessed') || st.includes('unverified')) { cScore += 4; cNotes.push(`Email status ${emailObj.status} +4`); }
  if (phoneArr.length){ cScore += 4; cNotes.push('Has phone +4'); }
  if (linkedin){ cScore += 4; cNotes.push('Has LinkedIn +4'); }
  if (cScore > W.contactability) cScore = W.contactability;

  // Seniority (0–20)
  const sScore = Math.min(mapSeniority(title, seniority), W.seniority);

  // Title keywords (0–12)
  const tkRaw = Math.min(tMatches.length * 4, W.titleKeywords);
  const tkNotes = tMatches.length ? [`Title matches: ${tMatches.join(', ')} +${tkRaw}`] : [];

  // Department (0–8)
  const depHit = ICP.targetDepartments.some(d => norm(department) === norm(d) || norm(title).includes(norm(d)));
  const dScore = depHit ? W.department : 0;

  // Company size (0–10)
  let csScore = 0;
  if (typeof size === 'number'){
    if (withinRange(size, ICP.idealCompanySize)) csScore = W.companySize;
    else {
      const [min,max] = ICP.idealCompanySize;
      if ((size >= min/2 && size < min) || (size > max && size <= max*2)) csScore = 5;
    }
  }

  // Industry (0–8)
  const iScore = ICP.targetIndustries.some(ind => norm(industry).includes(norm(ind))) ? W.industry : 0;

  // Geo (0–6)
  const gScore = ICP.targetCountries.some(c => norm(country) === norm(c) || norm(country).includes(norm(c))) ? W.geo : 0;

  // Web reach (0–4)
  const wScore = alexaScore(alexa);

  // Recency (0–2)
  const rScore = (updYrs < 1) ? W.recency : 0;

  const score = Math.round(cScore + sScore + tkRaw + dScore + csScore + iScore + gScore + wScore + rScore);
  const grade = score >= 85 ? 'A' : score >= 70 ? 'B' : score >= 55 ? 'C' : 'D';

  const reasons = [
    ...cNotes,
    ...tkNotes,
    dScore ? `Department fit (${department}) +${W.department}` : `Department not in target +0`,
    typeof size === 'number'
      ? (csScore === W.companySize ? `Company size ${size} in ideal range +${W.companySize}`
         : csScore === 5 ? `Company size ${size} near ideal range +5`
         : `Company size ${size} outside ideal range +0`)
      : 'Company size unknown +0',
    iScore ? `Industry match (${industry}) +${W.industry}` : `Industry not prioritized +0`,
    gScore ? `Geo match (${country}) +${W.geo}` : `Geo outside target +0`,
    wScore ? `Web reach (Alexa ${alexa}) +${wScore}` : `Web reach unknown/low +0`,
    rScore ? `Recently updated (<12m) +${W.recency}` : `Not recently updated +0`,
  ];

  // ----- perfectly organized JSON payload per lead -----
  const formatted = {
    id: p.id ?? null,
    name: { first: p.first_name ?? null, last: p.last_name ?? null, full: name || null },
    role: { title: title || null, department: department || null, seniority: seniority || null },
    location: { city: city || null, country: country || null },
    contacts: {
      email: emailObj.value ? { value: emailObj.value, status: emailObj.status ?? null } : null,
      phones: phoneArr ?? [],
      linkedin: linkedin || null
    },
    company: {
      name: orgName || null,
      website: website || null,
      industry: industry || null,
      size: (typeof size === 'number' ? size : null),
      alexaRanking: (Number.isFinite(alexa) ? alexa : null),
    },
    scoring: {
      score, grade,
      breakdown: {
        contactability: cScore, seniority: sScore, titleKeywords: tkRaw, department: dScore,
        companySize: csScore, industry: iScore, geo: gScore, webReach: wScore, recency: rScore
      },
      positive: reasons.filter(r => !r.endsWith('+0')),
      negative: reasons.filter(r => r.endsWith('+0')),
      updatedAgeYears: Number.isFinite(updYrs) ? Number(updYrs.toFixed(2)) : null
    }
  };

  return { score, formatted };
}

// -------- main --------
const people = extractPeople($input.all());
if (!people.length) {
  return [{ json: { leads: [], note: 'No leads found in input.' } }];
}

// Score everyone
const scored = people.map(scorePerson);

// Sort DESC by score
scored.sort((a,b)=> b.score - a.score);

// Apply filters
let filtered = scored;
if (typeof FILTER.minScore === 'number') {
  filtered = filtered.filter(x => x.score >= FILTER.minScore);
}
if (typeof FILTER.topN === 'number' && FILTER.topN > 0) {
  filtered = filtered.slice(0, FILTER.topN);
}

// Emit one item per high-scoring lead, with perfectly structured JSON payload
const outItems = filtered.map((x, idx) => ({
  json: {
    rank: idx + 1,
    ...x.formatted
  }
}));

// If nothing met the threshold, still return an empty list hint
if (!outItems.length) {
  return [{ json: { leads: [], note: `No leads met minScore ${FILTER.minScore}.` } }];
}

return outItems;
